-- 1. test veritabanınızda employee isimli sütun bilgileri id(INTEGER), name VARCHAR(50), birthday DATE, email VARCHAR(100) olan bir tablo oluşturalım.
CREATE TABLE employee(id INTEGER,name VARCHAR(50),birthday DATE,email VARCHAR(100));

-- 2. Oluşturduğumuz employee tablosuna 'Mockaroo' servisini kullanarak 50 adet veri ekleyelim.

-- 3. Sütunların her birine göre diğer sütunları güncelleyecek 5 adet UPDATE işlemi yapalım.
UPDATE employee SET name = 'Natty Martinovsky', email = 'nmartinovsky1i@purevolume.com' WHERE id = 2 RETURNING *;
UPDATE employee SET name = 'Weylin Presland', birthday = '1980-02-22' WHERE name = 'Raleigh Rubinowitch' RETURNING *;
UPDATE employee SET birthday = '1999-08-20', email = 'ifurmedge1g@washington.edu' WHERE birthday = '1988-09-22' RETURNING *;
UPDATE employee SET birthday = '1944-09-09', name = 'Farrand Benzies' WHERE id = 5 RETURNING *;
UPDATE employee SET name = 'Thoma Spacey', email = 'tspacey1e@sciencedirect.com' WHERE email = 'nrallinshaw5@altervista.org' RETURNING *;

-- 4. Sütunların her birine göre ilgili satırı silecek 5 adet DELETE işlemi yapalım.
DELETE FROM employee WHERE name = 'Kaylil Gruszecki' RETURNING *;
DELETE FROM employee WHERE id = 10 RETURNING *;
DELETE FROM employee WHERE birthday = '1921-04-14' RETURNING *;
DELETE FROM employee WHERE email = 'rmathesa@businessinsider.com' RETURNING *;
DELETE FROM employee WHERE name ='Alta Bruhke' RETURNING *;